          <div className="crud-table-card">
            <h2 className="table-title">Manage Fees</h2>
          {feeLoading ? (
            <p>Loading...</p>
          ) : (
            <table className="crud-table styled-table">
              <thead>
                <tr>
                  <th>#</th>
                  <th>Student</th>
